class NotificationSearchDelegate extends SearchDelegate {
  final List<Map<String, dynamic>> notifications;

  NotificationSearchDelegate(this.notifications);

  @override
  List<Widget>? buildActions(BuildContext context) {
    return [
      IconButton(
        icon: const Icon(Icons.clear),
        onPressed: () {
          query = '';
          close(context, null);
        },
      ),
    ];
  }

  @override
  Widget? buildLeading(BuildContext context) {
    return IconButton(
      icon: const Icon(Icons.arrow_back),
      onPressed: () {
        close(context, null);
      },
    );
  }

  @override
  Widget buildResults(BuildContext context) {
    final results = notifications.where((notification) {
      return notification['name']
          .toLowerCase()
          .contains(query.toLowerCase());
    }).toList();

    if (results.isEmpty) {
      return const Center(
        child: Text('Không tìm thấy kết quả.'),
      );
    }

    return ListView.builder(
      itemCount: results.length,
      itemBuilder: (context, index) {
        final notification = results[index];
        return ListTile(
          leading: CircleAvatar(
            backgroundImage: NetworkImage(notification['avatar']),
          ),
          title: Text(notification['name']),
          subtitle: Text(notification['content']),
        );
      },
    );
  }

  @override
  Widget buildSuggestions(BuildContext context) {
    final suggestions = notifications.where((notification) {
      return notification['name']
          .toLowerCase()
          .contains(query.toLowerCase());
    }).toList();

    return ListView.builder(
      itemCount: suggestions.length,
      itemBuilder: (context, index) {
        final notification = suggestions[index];
        return ListTile(
          leading: CircleAvatar(
            backgroundImage: NetworkImage(notification['avatar']),
          ),
          title: Text(notification['name']),
          subtitle: Text(notification['content']),
          onTap: () {
            query = notification['name'];
            showResults(context);
          },
        );
      },
    );
  }
}

